<script>
	var watchlist = localStorage.getItem('watchlist') ? JSON.parse(localStorage.getItem('watchlist')) : [];
	if(!watchlist[0]){
		$('#keep_watching').remove();
	}
	watchlist.sort( function ( a, b ) { return b.created_at - a.created_at; } );
	
	watchlist.forEach(function(watched, x) {
		
		season 			= '';
		episode 		= '';
		serial_slug		= '';
		type			= '';
		id				= '';
		slug			= '';
		thumbnail		= '';
		season_episode	= '';
		
		
		if(watched.episode){
			episodename 		= watched.episode;
			getepisode 			= episodename.slice(-5);
			episode 			= getepisode.replace(/\D/g,'');
		}
		
		if(watched.season){ season = watched.season; }
		if(episode){ serial_slug = '?season='+ season +'&episode='+ episode; }
		if(watched.type){ type = watched.type; }
		if(watched.id){ id = watched.id; }
		if(watched.slug){ slug = watched.slug; }
		if(watched.thumb){ thumbnail = 'https://static.movie.ge/movies/bg/big/'+watched.thumb; }
		
		url = '/'+ type +'/'+ id +'/'+ slug +''+ serial_slug +'';
		
		progress = (watched.start * 100) / watched.end;
		
		
		
		if(watched.episode && season){
			
			season_episode = 'სეზონი '+ season+' - '+watched.episode;
		}
		
		$('#keepContainer').append( 
			'<div class="swiper-slide">'+
			' <div class="keeping_ithem align-self-center continue-item">'+
			'	<div class="position-relative keep-watching">'+
			'	   <img src="/theme/img/nothumbnail.png" data-src="'+ thumbnail +'" class="cover-img lazy_load" alt="">'+
			'	   <div class="overlay keeping_overlay center text-center pt-5">'+
			'		  <a class="d-flex flex-column justify-content-center w-100 h-100" href="'+ url +'">'+
			'			 <p class="text-main mb-0 center arial">'+ season_episode +'</p>'+
			'			 <p class="text-main center font-size-20">'+ watched.start_time +'</p>'+
			'		  </a>'+
			'	   </div>'+
			'	   <div class="keep-watching-title text-main font-size-14 arial d-flex align-items-center">'+
			'		  <div class="progress_bar"><span class="progress" style="width:'+ progress +'%"></span></div>'+
			'		  <a href="'+ url +'" class="text-main font-size-15 px-2">'+ watched.title_ge +'</a>  '+
			'		  <div class="flex-grow-1"></div>'+
			'	   </div>'+
			'	</div>'+
			' </div>'+
			'</div>'
		);
		
	});
	
	
	
