var add_watchlist_item = function ($object) {
	
	var exist_item =  watchlist.find(x => x.id === $object.id );
	var max_length = 20;
	var item_id = watchlist.indexOf(exist_item);
	
	if( watchlist.length > max_length ){
		watchlist.shift();
