	if( watchlist[item_id] ){
		watchlist[item_id] = $object;
	}else if( watchlist.indexOf(exist_item) < 0 ){
		watchlist.push($object);
	}

	localStorage.setItem('watchlist' , JSON.stringify(watchlist));
	
}


