<script>
$(document).ready(function(){

	$('#stars li').on('mouseover', function(){
		
		var onStar = parseInt($(this).data('value'), 10);

		$(this).parent().children('li.star').each(function(e){
			if (e < onStar) {
				$(this).addClass('hover');
			}else{
				$(this).removeClass('hover');
			}
		});

	}).on('mouseout', function(){
		$(this).parent().children('li.star').each(function(e){
			$(this).removeClass('hover');
		});
	});


	$('#stars li').on('click', function(){

		var onStar = parseInt($(this).data('value'), 10);
		var stars = $(this).parent().children('li.star');

		for (i = 0; i < stars.length; i++) {
			$(stars[i]).removeClass('selected');
		}

		for (i = 0; i < onStar; i++) {
			$(stars[i]).addClass('selected');
		}

		var ratingValue = parseInt($('#stars li.selected').last().data('value'), 10);
		var msg = "";
		
		if (ratingValue > 1) {

			msg = "Thanks! You rated this " + ratingValue + " stars.";
			action = 'set_rating';
			$.ajax({
				url: '/profile/rating',
				type: 'POST',
				dataType: 'json',
				data: { id: parseInt('835'),type: "movie", rating: ratingValue, action:action },
			}).done(function(data){
				if(data.msg){
					users_count = $('.users_count span').text();
					
					$('.users_count span').html(data.all_rating);
					$('.rating_count').html(data.rating);
					Notify(data.msg);
				}else{
					Notify(data.errors, 'danger');
				}
			}).fail(function(error){
				var response = error.responseJSON;
				if( response.errors ) Notify( response.errors ,'danger');
			});

		}

		responseMessage(msg);

	});

  
});


function responseMessage(msg) {
  $('.success-box').fadeIn(200);  
  $('.success-box div.text-message').html("<span>" + msg + "</span>");
}


