		type:'movie'
	},
})


</script>

<script>

	// More/Info Dropdowns/Overlays Toggle functions
	let body = document.querySelector('.body');
	let ToggleBtns = document.querySelectorAll('.dropdown-btn');
	let ToggleMenus = document.querySelectorAll('.dropdown-menu');
	let infoBtns = document.querySelectorAll('.info');
	body.addEventListener('mouseup', function(e) {
		ToggleMenus.forEach(menu => {
			if (!menu.parentNode.contains(e.target)) {
				menu.parentNode.classList.remove('active');
			}
		});
		infoBtns.forEach(btn => {
			if (!btn.parentNode.contains(e.target)) {
				btn.classList.remove('shown');
			}
		});
	});
	ToggleBtns.forEach(btn => {
		btn.addEventListener('click', function(e) {
			btn.parentNode.classList.toggle('active');
		});
	});
	infoBtns.forEach(btn => {
		btn.addEventListener('click', function() {
			btn.classList.toggle('shown');
		});
	});



	// Movie Problem Overlay Show/Hide function
	let probBtns = document.querySelectorAll('.problem-btn');
	let probOverlay = document.querySelector('.problem-overlay');
	let cancelBtnAlt = document.querySelector('.cancel-btn-alt');
	probBtns.forEach(btn => {
		btn.addEventListener('click', function() {
			probOverlay.classList.add('show');
		});
	});
	if(cancelBtnAlt){
		cancelBtnAlt.addEventListener('click', function() {
		probOverlay.classList.remove('show');
	});
	}
	
</script>	
